import React, { useState, useEffect, useMemo } from "react";
import axios from "axios";
import { AgGridReact } from "ag-grid-react";
import "ag-grid-community/styles/ag-grid.css";
import "ag-grid-community/styles/ag-theme-alpine.css";
import "./AgGridColors.css";

const AgGridTable = () => {
  const [projects, setProjects] = useState([]);
  const [selected, setSelected] = useState("");
  const [rowData, setRowData] = useState([]);
  const [columnDefs, setColumnDefs] = useState([]);

  useEffect(() => {
    axios.get("http://localhost:8000/projects").then((res) => setProjects(res.data));
  }, []);

  useEffect(() => {
    if (selected) {
      axios.get(`http://localhost:8000/project/${selected}`).then((res) => {
        const data = res.data;
        setRowData(data);

        // Definierte Spalten mit Header-Klassen und Editierbarkeit
        const colSetup = [
          { field: "Call-ID", headerClass: "header-Call_ID" },
          { field: "Ort", headerClass: "header-Ort" },
          { field: "Magische-Relevanz", headerClass: "header-Magische_Relevanz" },
          { field: "Gebäude", headerClass: "header-Gebäude" },
          { field: "Bauteil", editable: true, headerClass: "header-Bauteil" },
          { field: "Gewicht", headerClass: "header-Gewicht" },
          { field: "Material", headerClass: "header-Material" },
          { field: "Größe", headerClass: "header-Größe" },
          { field: "Spell", headerClass: "header-Spell" },
          { field: "Hersteller", headerClass: "header-Hersteller" },
          { field: "Lieferant", headerClass: "header-Lieferant" },
          { field: "Einbauort", headerClass: "header-Einbauort" },
          { field: "Beschreibung", editable: true, headerClass: "header-Beschreibung" },
          { field: "Revision", editable: true, headerClass: "header-Revision" }
        ];
        setColumnDefs(colSetup);
      });
    }
  }, [selected]);

  return (
    <div>
      <h2>Projekt auswählen</h2>
      <select onChange={(e) => setSelected(e.target.value)} value={selected}>
        <option value="">-- wählen --</option>
        {projects.map((p) => (
          <option key={p} value={p}>{p}</option>
        ))}
      </select>
      <div className="ag-theme-alpine" style={{ height: 600, marginTop: 20 }}>
        <AgGridReact
          rowData={rowData}
          columnDefs={columnDefs}
          defaultColDef={{ resizable: true, sortable: true }}
        />
      </div>
    </div>
  );
};

export default AgGridTable;
